# print(f"{i - 1 =} ", nums[i-1] , f"{i =} ", nums[i] , f"{i + 1 =} ", nums[i+1])
# 334. Increasing Triplet Subsequence

from typing import List


class Solution:
    def increasingTriplet(self, nums: List[int]) -> bool:
        for i in range(0,len(nums)):
            try:
                if nums[i-1] < nums[i] < nums[i+1] and i - 1 >= 0:
                    return True
            except:
                pass
        return False        

